INSERT INTO `TRENO` (`capienza_massima`, `velocita_massima`, `anno`, `tipologia`,`nome`,`descrizione`,`immagine`)
VALUES
	(370,null,1982,'Antico','VENICE SIMPLON - ORIENT EXPRESS',"Gli amanti del glamour andranno in brodo di giuggiole da Venezia a Londra. Il lusso trionfa, dall'arredo sontuoso del vagone ristorante (argenteria francese, tovaglie di lino e bicchieri di cristallo) al vagone piano bar,Signore, le scarpe di Manolo Blahnik sono l'ideale per fare la passerella nelle cittàpiù romantiche d'Europa che l'Orient Express degna con la sua presenza: Vienna, Parigi, Praga e Istanbul.",'http://localhost/HW1/foto/foto2/VENICE%20SIMPLON%20-%20ORIENT%20EXPRESS.jpg'),
	(190,null,1982,'Antico','GHAN', "La saga del Ghan ebbe inizio nel 1877, quando l'originale linea ferroviaria Adelaide-Alice Springs-Darwin fu costruita nel posto sbagliato. Nel corso del suo primo secolo di vita attraversava una pianura alluvionale, Dopo le piogge spesso i convogli si impantanavano nell'outback. Nel 1980 si inaugurò una linea alternativa che collega il temperato sud dell'Australia con il tropicale nord",'http://localhost/HW1/foto/foto2/GHAN.jpg'),
	(150,null,1982,'Antico','FERROCARRIL CHIHUAHUA PACÍFICO', "Il Ferrocarril Chihuahua Pacífico vanta 36 ponti e 87 gallerie distribuite su 655 km di binari. Collega il montuoso entroterra del Messico settentrionale con la costa del Pacifico, attraversando canyon dalle pareti verticali, cascate e pianure quasi desertiche. Sono due i treni che servono questa tratta tra Los Mochis e Chihuahua: il primera express (il treno di prima classe) dispone di un ristorante, di un bar e di sedili reclinabili, e fa meno fermate del clase economica.", 'http://localhost/HW1/foto/foto2/FERROCARRIL%20CHIHUAHUA%20PAC%c3%8dFICO.jpg'),
	(170,null,1982,'Antico','OUTENIQUA CHOO-TJOE', "In funzione dal 1928, questo treno a vapore procede scoppiettando senza fretta. Da Knysna sbuffa lungo la costa dell'Oceano Indiano, passa dalla città di Wilderness, con le sue ampie spiagge, attraversa il Kaimans Bridge e, ansimando, affronta la gola che porta a George. Il viaggio, andata e ritorno, dura circa 7 ore e mezzo",'http://localhost/HW1/foto/foto2/OUTENIQUA%20CHOO-TJOE.jpg'),
	(290,null,1982,'Antico','EL NARIZ DEL DIABLO', "Andando verso sud da Riobamba, la ferrovia conosciuta come El Nariz del Diablo (il naso del diavolo) collega Alausí a Sibambe. I lavori di costruzione iniziarono nel 1908 quando vennero scavati i tornanti che avrebbero permesso al treno di coprire i quasi 1000 m di dislivello che separano Sibambe da Alausí, quota 2607 m. I più spericolati affrontano il naso del diavolo sul tetto del treno. Ma lo spazio tra il loro sombrero e la volta delle gallerie è davvero scarso",'http://localhost/HW1/foto/foto2/EL%20NARIZ%20DEL%20DIABLO.jpg'),
	(390,null,1982,'Antico','TRANSIBERIANA', "Parte ogni martedì dalla stazione di Yaroslavl di Mosca, attraversa un terzo del pianeta e conclude la sua corsa nell'affascinante e decadente Vladivostok. Costeggia il Lago Baikal, che sembra apparire dal nulla in mezzo alla taiga siberiana, Deviando dalla linea principale, la transmongolica attraversa un paesaggio di tipiche abitazioni russe e distese di foreste che cedono il passo alle infinite steppe e ai cieli della Mongolia. Il treno prosegue poi fino a Pechino, passando dalla spettacolare Grande Muraglia.",'http://localhost/HW1/foto/foto2/TRANSIBERIANA.jpg'),
	(450,null,1982,'Antico','ROCKY MOUNTAINEER', "Questo treno attraversa in due giorni le incantevoli Canadian Rockies (Montagne Rocciose canadesi). Si viaggia di giorno, in modo da ammirare canyon, fiumi, valli e laghi glaciali. Si parte dalla costiera Vancouver. Poi si entra nel regno delle Rockies passando da Jasper, Banff e Calgary fino a raggiungere l'Albertaj",'http://localhost/HW1/foto/foto2/ROCKY%20MOUNTAINEER.jpg'),
	(175,null,1982,'Antico','COAST STARLIGHT', "Attraverso la costa occidentale degli Stati Uniti, la Starlight tocca alcune tra le città più importanti del paese: Seattle, Portland e Los Angeles. Il viaggio dura 35 ore e tocca tre stati: Washington, Oregon e California.",'http://localhost/HW1/foto/foto2/COAST%20STARLIGHT.jpg');
